import React, { useEffect, useMemo, useState } from 'react';
import TopNav from '../components/TopNav';
import {
  Container,
  Card,
  Row,
  Col,
  Badge,
  Spinner,
  ListGroup,
  ProgressBar,
} from 'react-bootstrap';
import { getStudent, predictSingle } from '../services/api';
import { useParams } from 'react-router-dom';

const headerGradient =
  'linear-gradient(135deg, rgba(239,246,255,1) 0%, rgba(221,239,253,1) 40%, rgba(219,234,254,1) 100%)';

const chipStyle = {
  borderRadius: 999,
  padding: '3px 10px',
  backgroundColor: '#eff6ff',
  fontSize: '0.7rem',
  fontWeight: 500,
  marginRight: 6,
  marginBottom: 6,
  display: 'inline-block',
};

const RiskPill = ({ tier }) => {
  if (!tier) return null;
  const upper = String(tier).toUpperCase();
  const map = {
    HIGH: { variant: 'danger', label: 'High Risk' },
    MEDIUM: { variant: 'warning', label: 'Medium Risk' },
    LOW: { variant: 'success', label: 'Low Risk' },
  };
  const cfg = map[upper] || { variant: 'secondary', label: upper };

  return (
    <Badge bg={cfg.variant} pill className="ms-2">
      {cfg.label}
    </Badge>
  );
};

const Gauge = ({ label, value, suffix = '', variant = 'info' }) => {
  const numeric = Number(value) || 0;
  const safe = Math.max(0, Math.min(100, numeric));

  return (
    <div className="mb-3">
      <div className="d-flex justify-content-between">
        <span className="small text-muted">{label}</span>
        <span className="small">
          <strong>{safe.toFixed(1)}</strong>
          {suffix}
        </span>
      </div>
      <ProgressBar
        now={safe}
        variant={variant}
        style={{ height: 8 }}
        className="rounded-pill"
      />
    </div>
  );
};

// ✅ helper to safely parse explanation JSON or string
const parseExplanation = (raw) => {
  if (!raw) return null;
  try {
    return typeof raw === 'string' ? JSON.parse(raw) : raw;
  } catch {
    return { raw };
  }
};

export default function StudentProfile() {
  const { id } = useParams();
  const [s, setS] = useState(null);
  const [pred, setPred] = useState(null);
  const [loading, setLoading] = useState(true);

  const buildPayloadFromStudent = (stu) => {
    const { id, ...rest } = stu;
    return rest;
  };

  useEffect(() => {
    const fetchData = async () => {
      if (!id) return;
      try {
        setLoading(true);
        const stu = await getStudent(id);
        setS(stu);

        try {
          const payload = buildPayloadFromStudent(stu);
          const p = await predictSingle(payload);

          setPred({
            dropout_probability: Number(p.dropout_probability),
            deservingness_score: Number(p.deservingness_score),
            risk_tier: p.risk_tier,
            explanation: parseExplanation(p.explanation),
          });
        } catch (e) {
          console.error('Prediction error', e);
          setPred(null);
        }
      } catch (e) {
        console.error(e);
        setS(null);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  const derived = useMemo(() => {
    if (!s) {
      return { attendance: 0, academic: 0, motivation: 0 };
    }
    return {
      attendance: Number(s.attendance_rate ?? s.attendanceRate ?? 0),
      academic: Number(s.academic_score ?? s.academicScore ?? 0),
      motivation: Number(s.motivation_level ?? s.motivational_score ?? 0),
    };
  }, [s]);

  if (loading) {
    return (
      <>
        <TopNav />
        <Container className="p-4">
          <Card className="p-4 text-center shadow-sm">
            <Spinner animation="border" className="me-2" />
            Loading student profile…
          </Card>
        </Container>
      </>
    );
  }

  if (!s) {
    return (
      <>
        <TopNav />
        <Container className="p-4">
          <Card className="p-4 text-center shadow-sm">
            Unable to load student profile.
          </Card>
        </Container>
      </>
    );
  }

  const riskVariant =
    pred?.risk_tier === 'HIGH'
      ? 'danger'
      : pred?.risk_tier === 'MEDIUM'
      ? 'warning'
      : 'success';

  const dropoutPct = pred
    ? (Number(pred.dropout_probability || 0) * 100).toFixed(1)
    : '0.0';

  const deservingScore = pred
    ? Number(pred.deservingness_score || 0).toFixed(2)
    : '0.00';

  const reasons =
    pred?.explanation?.human_readable_reasons ||
    pred?.explanation?.reasons ||
    [];

  const features =
    pred?.explanation?.global_top_model_features ||
    pred?.explanation?.top_features ||
    [];

  return (
    <>
      <TopNav />
      <div
        style={{
          minHeight: '100vh',
          background:
            'linear-gradient(180deg, #eff6ff 0%, #ffffff 40%, #f9fafb 100%)',
          fontFamily: '"Inter", system-ui, -apple-system, BlinkMacSystemFont',
        }}
      >
        <Container className="py-4">
          {/* existing layout unchanged above */}

          {/* ✅ ADD REASONS SECTION */}
          {pred && (
            <Row className="mt-4">
              <Col>
                <Card className="shadow-sm border-0" style={{ borderRadius: 18 }}>
                  <Card.Body>
                    <Card.Title className="mb-2">
                      Why this prediction?
                    </Card.Title>

                    {features.length > 0 && (
                      <>
                        <div className="small text-muted mb-1">
                          Top influencing features:
                        </div>
                        <div className="mb-3">
                          {features.map((f, i) => (
                            <Badge
                              bg="light"
                              text="dark"
                              key={i}
                              className="me-2 mb-2"
                            >
                              {f}
                            </Badge>
                          ))}
                        </div>
                      </>
                    )}

                    {reasons.length > 0 ? (
                      <>
                        <div className="small text-muted mb-1">
                          Human-readable explanation:
                        </div>
                        <ul className="small text-muted mb-0">
                          {reasons.map((r, i) => (
                            <li key={i}>{r}</li>
                          ))}
                        </ul>
                      </>
                    ) : (
                      <p className="text-muted small mb-0">
                        No explanation available for this prediction.
                      </p>
                    )}
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          )}
        </Container>
      </div>
    </>
  );
}
