// src/pages/Students.jsx
import React, { useEffect, useMemo, useState } from "react";
import TopNav from "../components/TopNav";
import {
  Container,
  Card,
  Table,
  Row,
  Col,
  Form,
  Badge,
  InputGroup,
  Button,
  Modal,
  Spinner,
} from "react-bootstrap";
import { getStudents, deleteStudent, predictSingle } from "../services/api";
import { useNavigate } from "react-router-dom";

const SummaryPill = ({ label, value, variant }) => (
  <Badge
    bg={variant}
    pill
    className="me-2 mb-2"
    style={{ fontSize: "0.70rem", fontWeight: 600 }}
  >
    {label}: {value}
  </Badge>
);

const RiskTierBadge = ({ tier }) => {
  if (!tier) return <span className="text-muted small">—</span>;
  const t = String(tier).toUpperCase();
  let variant = "secondary";
  if (t === "HIGH") variant = "danger";
  else if (t === "MEDIUM") variant = "warning";
  else if (t === "LOW") variant = "success";

  return (
    <Badge
      bg={variant}
      pill
      className="small"
      style={{ fontSize: "0.70rem", minWidth: 55, textAlign: "center" }}
    >
      {t}
    </Badge>
  );
};

const pageBackground =
  "linear-gradient(180deg, #eff6ff 0%, #ffffff 40%, #f9fafb 100%)";

// -----------------------------------------------------------------------------
// MAIN COMPONENT — Students Registry
// -----------------------------------------------------------------------------

export default function Students() {
  const [list, setList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [scoring, setScoring] = useState(false);

  const [search, setSearch] = useState("");
  const [schoolFilter, setSchoolFilter] = useState("ALL");
  const [riskFilter, setRiskFilter] = useState("ALL");

  const [sortKey, setSortKey] = useState("name");
  const [sortDir, setSortDir] = useState("asc");

  const [deletingId, setDeletingId] = useState(null);

  const [showExplain, setShowExplain] = useState(false);
  const [explainStudent, setExplainStudent] = useState(null);

  const nav = useNavigate();

  const buildPayload = (s) => {
    const { id, ...rest } = s;
    return rest;
  };

  useEffect(() => {
    const load = async () => {
      try {
        setLoading(true);

        const students = await getStudents();
        const arr = Array.isArray(students) ? students : [];

        if (!arr.length) {
          setList([]);
          return;
        }

        setScoring(true);

        // fetch predictions same as Dashboard
        const enriched = await Promise.all(
          arr.map(async (s) => {
            try {
              const p = await predictSingle(buildPayload(s));
              return {
                ...s,
                dropout_probability: Number(p.dropout_probability),
                deservingness_score: Number(p.deservingness_score),
                risk_tier: p.risk_tier,
              };
            } catch {
              return {
                ...s,
                dropout_probability: 0,
                deservingness_score: 0,
                risk_tier: "LOW",
              };
            }
          })
        );

        setList(enriched);
      } catch (e) {
        console.error(e);
        setList([]);
      } finally {
        setScoring(false);
        setLoading(false);
      }
    };

    load();
  }, []);

  // ---------------- sorting, filtering, UI logic stays SAME ----------------

  const filtered = useMemo(() => {
    let rows = [...list];

    const q = search.trim().toLowerCase();
    if (q) {
      rows = rows.filter((s) =>
        `${s.name} ${s.district} ${s.preferred_course}`
          .toLowerCase()
          .includes(q)
      );
    }

    if (schoolFilter !== "ALL") {
      rows = rows.filter((s) =>
        String(s.private_or_govt_school).toLowerCase().includes(
          schoolFilter.toLowerCase()
        )
      );
    }

    if (riskFilter !== "ALL") {
      rows = rows.filter(
        (s) => String(s.risk_tier ?? "").toUpperCase() === riskFilter
      );
    }

    rows.sort((a, b) => {
      const get = (x) => {
        switch (sortKey) {
          case "dropout":
            return Number(x.dropout_probability ?? 0);
          case "deserving":
            return Number(x.deservingness_score ?? 0);
          default:
            return String(x[sortKey] ?? "").toLowerCase();
        }
      };
      const va = get(a),
        vb = get(b);
      if (typeof va === "number") {
        return sortDir === "asc" ? va - vb : vb - va;
      }
      return sortDir === "asc"
        ? va.localeCompare(vb)
        : vb.localeCompare(va);
    });

    return rows;
  }, [list, search, schoolFilter, riskFilter, sortKey, sortDir]);

  // --------------------------------------------------------------------------

  return (
    <>
      <TopNav />
      <div
        style={{
          minHeight: "100vh",
          background: pageBackground,
          fontFamily: '"Inter", system-ui',
        }}
      >
        <Container className="py-4">
          {loading ? (
            <Card className="p-4 text-center shadow-sm">
              <Spinner animation="border" className="me-2" />
              Loading students & predictions…
            </Card>
          ) : (
            <Card className="shadow-sm border-0" style={{ borderRadius: 18 }}>
              <Card.Body>
                <div className="table-responsive">
                  <Table hover className="align-middle mb-0">
                    <thead>
                      <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>District</th>
                        <th>Attendance</th>
                        <th>Dropout %</th>
                        <th>Deserving</th>
                        <th>Risk</th>
                        <th>Action</th>
                      </tr>
                    </thead>

                    <tbody>
                      {filtered.map((s, i) => {
                        const dropout = Number(s.dropout_probability || 0);
                        const deserving = Number(s.deservingness_score || 0);

                        return (
                          <tr key={s.id ?? i}>
                            <td>{i + 1}</td>
                            <td>{s.name}</td>
                            <td>{s.district}</td>
                            <td>{s.attendance_rate ?? "-"}%</td>
                            <td style={{ color: "#b91c1c", fontWeight: 600 }}>
                              {(dropout * 100).toFixed(1)}%
                            </td>
                            <td style={{ color: "#15803d", fontWeight: 600 }}>
                              {deserving.toFixed(2)}
                            </td>
                            <td>
                              <RiskTierBadge tier={s.risk_tier} />
                            </td>
                            <td>
                              <Button
                                size="sm"
                                variant="outline-primary"
                                onClick={() => nav(`/students/${s.id}`)}
                              >
                                View
                              </Button>
                            </td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </Table>
                </div>
              </Card.Body>
            </Card>
          )}
        </Container>
      </div>
    </>
  );
}
