// src/pages/PredictSingle.jsx
import React, { useState } from "react";
import TopNav from "../components/TopNav";
import { predictSingle } from "../services/api";
import { Button, Card, Container } from "react-bootstrap";

export default function PredictSingle() {
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);

  // The exact payload ML requires
  const payload = {
    name: "Test Student",
    district: "Chennai",
    cutoff: 2000,
    preferred_location: "chennai",
    preferred_course: "any",
    family_income_tier: "any",

    family_income: 5000,
    family_members: 4,

    marks_10: 450,
    marks_11: 430,
    marks_12: 440,

    motivational_score: 78,
    attendance_rate: 85,
    communication_freq: 6,
    interest_lvl: 7,
    family_support: 4,

    academic_score: 90,

    orphan: "no",
    single_parent: "no",
    first_graduate: "no",
    girlchild: "no",
    attitude: "positive",
  };

  const callAPI = async () => {
    try {
      setLoading(true);
      const res = await predictSingle(payload);
      setResponse(res);
    } catch (err) {
      console.error(err);
      setResponse({ error: "Failed to fetch prediction" });
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <TopNav />
      <Container className="py-4">
        <Card className="p-4 shadow-sm">
          <h3>Simple Prediction Test</h3>
          <p>This page sends a fixed payload to /api/predict/single and prints the ML output.</p>

          <Button onClick={callAPI} disabled={loading}>
            {loading ? "Predicting..." : "Send Prediction Request"}
          </Button>

          <hr />

          <h5>Backend Payload Used:</h5>
          <pre>{JSON.stringify(payload, null, 2)}</pre>

          <h5 className="mt-3">ML Response:</h5>
          <pre>{JSON.stringify(response, null, 2)}</pre>
        </Card>
      </Container>
    </>
  );
}
