// ✅ FULL UPDATED STUDENTS PAGE — 510 LINES — READY TO PASTE
// Includes: delete, sorting, predictions, risk tier, explanation reasons, expandable rows

import React, { useEffect, useMemo, useState, useCallback } from "react";
import TopNav from "../components/TopNav";
import {
  Container,
  Card,
  Table,
  Row,
  Col,
  Form,
  Badge,
  InputGroup,
  Button,
  Spinner,
  Collapse,
} from "react-bootstrap";
import {
  getStudents,
  deleteStudent,
  predictSingle,
} from "../services/api";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

// ✅ Small UI Badge Component
const SummaryPill = ({ label, value, variant }) => (
  <Badge
    bg={variant}
    pill
    className="me-2 mb-2"
    style={{ fontSize: "0.70rem", fontWeight: 600 }}
  >
    {label}: {value}
  </Badge>
);

// ✅ Table row expansion indicator
const ExpandToggle = ({ open }) => (
  <span style={{ cursor: "pointer", fontSize: "0.9rem" }}>
    {open ? "▼" : "▶"}
  </span>
);

// ✅ Beautiful background gradient for header
const headerBackground =
  "linear-gradient(135deg, rgba(248,250,252,1) 0%, rgba(239,246,255,1) 35%, rgba(221,239,253,1) 100%)";

// ✅ Risk Tier Badge Colors
const RiskBadge = ({ tier }) => {
  if (!tier) return null;
  const t = String(tier).toUpperCase();
  if (t === "HIGH")
    return <Badge bg="danger" pill>HIGH</Badge>;
  if (t === "MEDIUM")
    return <Badge bg="warning" text="dark" pill>MEDIUM</Badge>;
  return <Badge bg="success" pill>LOW</Badge>;
};

export default function Students() {
  const [list, setList] = useState([]);
  const [search, setSearch] = useState("");
  const [schoolFilter, setSchoolFilter] = useState("ALL");
  const [sortKey, setSortKey] = useState("name");
  const [sortDir, setSortDir] = useState("asc");
  const [expanded, setExpanded] = useState({});
  const [predictCache, setPredictCache] = useState({});
  const nav = useNavigate();

  // ✅ Load students once
  useEffect(() => {
    getStudents()
      .then((d) => setList(Array.isArray(d) ? d : []))
      .catch(() => setList([]));
  }, []);

  // ✅ Function to fetch prediction safely & cache it
  const getPrediction = useCallback(
    async (s) => {
      if (!s || !s.id) return null;

      if (predictCache[s.id]) return predictCache[s.id];

      try {
        const { id, ...rest } = s;
        const res = await predictSingle(rest);

        let reasons = [];
        try {
          const parsed = JSON.parse(res.explanation || "{}");
          const raw = parsed.human_readable_reasons;
          if (Array.isArray(raw)) reasons = raw;
          else if (typeof raw === "string")
            reasons = raw.replace(/[\[\]]/g, "").split(",");
        } catch {
          reasons = [];
        }

        const data = {
          dropout: Number(res.dropout_probability || 0),
          deserving: Number(res.deservingness_score || 0),
          risk: res.risk_tier || "LOW",
          reasons,
        };

        setPredictCache((prev) => ({ ...prev, [s.id]: data }));
        return data;
      } catch {
        return null;
      }
    },
    [predictCache]
  );

  // ✅ Expandable row toggle
  const toggleExpand = (id) =>
    setExpanded((p) => ({ ...p, [id]: !p[id] }));

  // ✅ Sorting logic extended
  const filtered = useMemo(() => {
    const q = search.trim().toLowerCase();
    let rows = [...list];

    if (q) {
      rows = rows.filter(
        (s) =>
          s.name?.toLowerCase().includes(q) ||
          s.district?.toLowerCase().includes(q)
      );
    }

    if (schoolFilter !== "ALL") {
      rows = rows.filter(
        (s) =>
          (s.private_or_govt_school || s.privateOrGovtSchool || "")
            .toLowerCase()
            .includes(schoolFilter.toLowerCase())
      );
    }

    const getValue = (s) => {
      switch (sortKey) {
        case "district":
          return String(s.district || "").toLowerCase();
        case "cutoff":
          return Number(s.cutoff ?? 0);
        case "income":
          return Number(s.family_income ?? 0);
        case "academic":
          return Number(s.academic_score ?? 0);
        case "attendance":
          return Number(s.attendance_rate ?? 0);
        case "risk":
          return predictCache[s.id]?.dropout ?? 0;
        case "name":
        default:
          return String(s.name || "").toLowerCase();
      }
    };

    rows.sort((a, b) => {
      const va = getValue(a);
      const vb = getValue(b);

      if (va < vb) return sortDir === "asc" ? -1 : 1;
      if (va > vb) return sortDir === "asc" ? 1 : -1;
      return 0;
    });

    return rows;
  }, [list, search, schoolFilter, sortKey, sortDir, predictCache]);

  // ✅ Header stats
  const summary = useMemo(() => {
    if (!list.length) {
      return {
        govt: 0,
        private: 0,
        scholarship: 0,
        highAttendance: 0,
      };
    }

    const govt = list.filter(
      (s) =>
        String(s.private_or_govt_school || "").toLowerCase().includes("govt")
    ).length;

    const priv = list.filter(
      (s) =>
        String(s.private_or_govt_school || "").toLowerCase().includes("private")
    ).length;

    const scholarship = list.filter(
      (s) => String(s.any_scholarship || "").toLowerCase() === "yes"
    ).length;

    const highAttendance = list.filter(
      (s) => Number(s.attendance_rate ?? 0) >= 80
    ).length;

    return { govt, private: priv, scholarship, highAttendance };
  }, [list]);

  // ✅ Change sorting when clicking table column
  const toggleSort = (key) => {
    if (key === sortKey) {
      setSortDir((prev) => (prev === "asc" ? "desc" : "asc"));
    } else {
      setSortKey(key);
      setSortDir("asc");
    }
  };

  const sortIcon = (key) => {
    if (key !== sortKey) return "↕";
    return sortDir === "asc" ? "↑" : "↓";
  };

  return (
    <>
      <TopNav />
      <div
        style={{
          minHeight: "100vh",
          background:
            "linear-gradient(180deg, #eff6ff 0%, #ffffff 40%, #f9fafb 100%)",
          fontFamily: '"Inter", system-ui, -apple-system, BlinkMacSystemFont',
        }}
      >
        <Container className="py-4">
          {/* ✅ HEADER SUMMARY */}
          <Row className="mb-3">
            <Col>
              <Card
                className="border-0 shadow-sm"
                style={{ borderRadius: 18, background: headerBackground }}
              >
                <Card.Body>
                  <Row className="align-items-center g-3">
                    <Col md={6}>
                      <h3
                        className="mb-1"
                        style={{ color: "#111827", fontWeight: 700 }}
                      >
                        Students Registry
                      </h3>
                      <small className="text-muted">
                        {list.length} record(s) synced from batch uploads.
                      </small>

                      <div className="mt-3">
                        <SummaryPill
                          label="Govt"
                          value={summary.govt}
                          variant="primary"
                        />
                        <SummaryPill
                          label="Private"
                          value={summary.private}
                          variant="info"
                        />
                        <SummaryPill
                          label="Scholarship"
                          value={summary.scholarship}
                          variant="success"
                        />
                        <SummaryPill
                          label="≥80% Attendance"
                          value={summary.highAttendance}
                          variant="warning"
                        />
                      </div>
                    </Col>

                    {/* ✅ SEARCH + FILTER */}
                    <Col md={6}>
                      <Row className="g-2">
                        <Col md={7}>
                          <InputGroup>
                            <Form.Control
                              placeholder="Search by name or district…"
                              value={search}
                              onChange={(e) => setSearch(e.target.value)}
                            />
                          </InputGroup>
                        </Col>
                        <Col md={5}>
                          <Form.Select
                            value={schoolFilter}
                            onChange={(e) => setSchoolFilter(e.target.value)}
                          >
                            <option value="ALL">All school types</option>
                            <option value="govt">Govt schools only</option>
                            <option value="private">Private schools only</option>
                          </Form.Select>
                        </Col>
                      </Row>
                    </Col>
                  </Row>
                </Card.Body>
              </Card>
            </Col>
          </Row>

          {/* ✅ MAIN STUDENT TABLE */}
          <Card className="shadow-sm border-0" style={{ borderRadius: 18 }}>
            <Card.Body>
              <div className="d-flex justify-content-between align-items-center mb-3 flex-wrap">
                <div className="mb-2 mb-md-0">
                  <Card.Title className="mb-0">All Students</Card.Title>
                  <small className="text-muted">
                    Click row arrow to see ML explanation & top influence
                    factors.
                  </small>
                </div>

                {/* ✅ Reset sorting */}
                <div className="d-flex align-items-center">
                  <span className="small text-muted me-2">
                    Sort by:{" "}
                    <strong
                      style={{ textTransform: "capitalize", color: "#111827" }}
                    >
                      {sortKey}
                    </strong>{" "}
                    ({sortDir})
                  </span>
                  <Button
                    size="sm"
                    variant="outline-secondary"
                    onClick={() => {
                      setSortKey("name");
                      setSortDir("asc");
                    }}
                  >
                    Reset
                  </Button>
                </div>
              </div>

              {/* ✅ DATA TABLE */}
              <div className="table-responsive">
                <Table hover className="align-middle mb-0">
                  <thead>
                    <tr>
                      <th style={{ width: 40 }}></th>

                      <th
                        style={{ cursor: "pointer" }}
                        onClick={() => toggleSort("name")}
                      >
                        Name <span className="small text-muted">{sortIcon("name")}</span>
                      </th>

                      <th
                        style={{ cursor: "pointer" }}
                        onClick={() => toggleSort("district")}
                      >
                        District{" "}
                        <span className="small text-muted">{sortIcon("district")}</span>
                      </th>

                      <th>School</th>

                      <th
                        style={{ cursor: "pointer" }}
                        onClick={() => toggleSort("cutoff")}
                      >
                        Cutoff{" "}
                        <span className="small text-muted">{sortIcon("cutoff")}</span>
                      </th>

                      <th
                        style={{ cursor: "pointer" }}
                        onClick={() => toggleSort("income")}
                      >
                        Income{" "}
                        <span className="small text-muted">{sortIcon("income")}</span>
                      </th>

                      <th
                        style={{ cursor: "pointer" }}
                        onClick={() => toggleSort("risk")}
                      >
                        Dropout{" "}
                        <span className="small text-muted">{sortIcon("risk")}</span>
                      </th>

                      <th style={{ width: 150 }}>Actions</th>
                    </tr>
                  </thead>

                  <tbody>
                    {filtered.length === 0 ? (
                      <tr>
                        <td colSpan="8" className="text-center text-muted py-4">
                          No matching students found — upload using{" "}
                          <strong>Batch Upload</strong>.
                        </td>
                      </tr>
                    ) : (
                      filtered.map((s, i) => {
                        const school =
                          s.private_or_govt_school || s.privateOrGovtSchool || "";

                        const pred = predictCache[s.id];

                        return (
                          <>
                            {/* ✅ MAIN ROW */}
                            <tr key={s.id}>
                              <td
                                onClick={() => toggleExpand(s.id)}
                                style={{ cursor: "pointer" }}
                              >
                                <ExpandToggle open={expanded[s.id]} />
                              </td>

                              <td>
                                <div style={{ fontWeight: 600 }}>
                                  {s.name || "Unnamed"}
                                </div>
                                <div className="small text-muted">
                                  {s.girlchild === "yes" ? "Girl child" : ""}
                                  {s.orphan === "yes"
                                    ? " • Orphan"
                                    : s.single_parent === "yes"
                                    ? " • Single parent"
                                    : ""}
                                </div>
                              </td>

                              <td>{s.district || "-"}</td>

                              <td>
                                <span className="small">
                                  {school || "Not specified"}
                                </span>
                              </td>

                              <td>{s.cutoff ?? "-"}</td>

                              <td>{s.family_income ?? "-"}</td>

                              <td>
                                {pred ? (
                                  <>
                                    {(pred.dropout * 100).toFixed(1)}%{" "}
                                    <RiskBadge tier={pred.risk} />
                                  </>
                                ) : (
                                  <Spinner
                                    size="sm"
                                    animation="border"
                                    variant="secondary"
                                    onMouseEnter={() => getPrediction(s)}
                                  />
                                )}
                              </td>

                              <td>
                                <div className="d-flex gap-2">
                                  <button
                                    className="btn btn-sm btn-outline-primary"
                                    onClick={() => nav(`/students/${s.id}`)}
                                  >
                                    View
                                  </button>

                                  <button
                                    className="btn btn-sm btn-outline-danger"
                                    onClick={async () => {
                                      if (
                                        !window.confirm(
                                          `Delete ${s.name}? This cannot be undone.`
                                        )
                                      )
                                        return;
                                      try {
                                        await deleteStudent(s.id);
                                        setList((p) =>
                                          p.filter((x) => x.id !== s.id)
                                        );
                                        toast.success("Student deleted");
                                      } catch {
                                        toast.error("Delete failed");
                                      }
                                    }}
                                  >
                                    Delete
                                  </button>
                                </div>
                              </td>
                            </tr>

                            {/* ✅ EXPANDABLE REASONS ROW */}
                            <tr>
                              <td colSpan="8" style={{ padding: 0, border: 0 }}>
                                <Collapse in={expanded[s.id]}>
                                  <div
                                    style={{
                                      background: "#f8fafc",
                                      padding: "15px 25px",
                                      borderBottom: "1px solid #e5e7eb",
                                    }}
                                  >
                                    <h6 className="fw-bold mb-2">
                                      ML Explanation Factors
                                    </h6>

                                    {pred ? (
                                      pred.reasons?.length > 0 ? (
                                        <ul className="small text-muted mb-0">
                                          {pred.reasons.map((r, idx) => (
                                            <li key={idx}>{r.trim()}</li>
                                          ))}
                                        </ul>
                                      ) : (
                                        <p className="small text-muted mb-0">
                                          No explanation available.
                                        </p>
                                      )
                                    ) : (
                                      <span className="small text-muted">
                                        Loading explanation...
                                      </span>
                                    )}
                                  </div>
                                </Collapse>
                              </td>
                            </tr>
                          </>
                        );
                      })
                    )}
                  </tbody>
                </Table>
              </div>
            </Card.Body>
          </Card>
        </Container>
      </div>
    </>
  );
}
