// src/pages/PredictSingle.jsx
import React, { useState } from "react";
import TopNav from "../components/TopNav";
import {
  Container,
  Row,
  Col,
  Card,
  Form,
  Button,
  Badge,
  OverlayTrigger,
  Tooltip,
  ProgressBar,
} from "react-bootstrap";
import { predictSingle } from "../services/api";
import { toast } from "react-toastify";

// ---------------- UI Helpers ----------------
const InfoPill = ({ label, value, variant = "secondary" }) => {
  if (!value && value !== 0) return null;
  return (
    <Badge
      bg={variant}
      pill
      className="me-2 mb-2"
      style={{ fontSize: "0.7rem", fontWeight: 600 }}
    >
      {label}: {value}
    </Badge>
  );
};

const Gauge = ({ label, value, suffix = "", variant = "info" }) => {
  const numeric = Number(value) || 0;
  const clamped = Math.max(0, Math.min(100, numeric));
  return (
    <div className="mb-3">
      <div className="d-flex justify-content-between align-items-center">
        <span className="small text-muted">{label}</span>
        <span className="small">
          <strong>{clamped.toFixed(1)}</strong>
          {suffix}
        </span>
      </div>
      <ProgressBar
        now={clamped}
        variant={variant}
        style={{ height: 8 }}
        className="rounded-pill mt-1"
      />
    </div>
  );
};

// ------------- RAW PAYLOAD ONLY (NO CHANGES) -------------
const buildPayloadForModel = (f) => ({
  name: f.name,
  district: f.district,

  cutoff: Number(f.cutoff),
  preferred_location: f.preferred_location,
  preferred_course: f.preferred_course,
  family_income_tier: f.family_income_tier,

  family_income: Number(f.family_income),
  family_members: Number(f.family_members),

  marks_10: Number(f.marks_10),
  marks_11: Number(f.marks_11),
  marks_12: Number(f.marks_12),

  passed_out_10: Number(f.passed_out_10),
  passed_out_11: Number(f.passed_out_11),
  passed_out_12: Number(f.passed_out_12),

  academic_score: Number(f.academic_score),
  motivational_score: Number(f.motivational_score || f.motivation_level),
  attendance_rate: Number(f.attendance_rate),

  communication_freq: Number(f.communication_freq),
  interest_lvl: Number(f.interest_lvl),
  family_support: Number(f.family_support),

  orphan: f.orphan,
  single_parent: f.single_parent,
  first_graduate: f.first_graduate,
  girlchild: f.girl_child || f.girlchild,

  attitude: f.attitude,
});

// explanation parser
const parseExplanation = (exp) => {
  if (!exp) return {};
  try {
    return JSON.parse(exp);
  } catch {
    return { raw: exp };
  }
};

// ---------------- Component ----------------
export default function PredictSingle() {
  const [form, setForm] = useState({
    name: "",
    district: "Chennai",

    cutoff: 0,
    preferred_location: "any",
    preferred_course: "any",
    family_income_tier: "any",

    passed_out_10: 2022,
    passed_out_11: 2023,
    passed_out_12: 2024,

    marks_10: 80,
    marks_11: 80,
    marks_12: 80,

    family_income: 20000,
    family_members: 4,

    academic_score: 75,
    motivational_score: 60,
    motivation_level: 3,
    attendance_rate: 80,

    orphan: "No",
    single_parent: "No",
    girlchild: "No",
    first_graduate: "Yes",

    communication_freq: 5,
    interest_lvl: 5,
    family_support: 5,

    attitude: "positive",
  });

  const [result, setResult] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  const update = (k, v) => setForm((p) => ({ ...p, [k]: v }));

  // ---------------- SUBMIT (RAW PAYLOAD) ----------------
  const submit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      const payload = buildPayloadForModel(form);
      const res = await predictSingle(payload);

      setResult({
        ...res,
        explanation: parseExplanation(res.explanation),
      });

      toast.success("Prediction success");
    } catch (err) {
      console.error(err);
      toast.error("Prediction failed");
    } finally {
      setSubmitting(false);
    }
  };

  const dropoutPct = result
    ? (Number(result.dropout_probability) * 100).toFixed(1)
    : "0.0";

  const deservingScore = result
    ? Number(result.deservingness_score).toFixed(2)
    : "0.00";

  const pageBackground =
    "linear-gradient(180deg,#eff6ff 0%,#fff 40%,#f9fafb 100%)";

  return (
    <>
      <TopNav />
      <div
        style={{
          minHeight: "100vh",
          background: pageBackground,
        }}
      >
        <Container className="py-4">
          {/* HEADER */}
          <Row className="mb-3">
            <Col>
              <Card className="border-0 shadow-sm" style={{ borderRadius: 18 }}>
                <Card.Body>
                  <h3 style={{ fontWeight: 700 }}>Single Student Prediction</h3>
                  <small className="text-muted">
                    Direct ML Prediction â€” no rule engine, no overrides.
                  </small>
                </Card.Body>
              </Card>
            </Col>
          </Row>

          {/* MAIN FORM */}
          <Form onSubmit={submit}>
            <Row className="g-4">
              <Col lg={8}>
                {/* Academic */}
                <Card className="p-3 mb-3 shadow-sm border-0" style={{ borderRadius: 18 }}>
                  <h5>Academic Information</h5>
                  <Row>
                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Academic Score</Form.Label>
                        <Form.Control
                          type="number"
                          value={form.academic_score}
                          onChange={(e) => update("academic_score", +e.target.value)}
                        />
                      </Form.Group>
                    </Col>

                    <Col md={4}>
                      <Form.Group>
                        <Form.Label>Motivational Score</Form.Label>
                        <Form.Control
                          type="number"
                          value={form.motivational_score}
                          onChange={(e) => update("motivational_score", +e.target.value)}
                        />
                      </Form.Group>
                    </Col>

                    <Col md={4}>
                      <Form.Group>
                        <Form.Label>Attendance Rate</Form.Label>
                        <Form.Control
                          type="number"
                          value={form.attendance_rate}
                          onChange={(e) => update("attendance_rate", +e.target.value)}
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                </Card>

                {/* Family */}
                <Card className="p-3 mb-3 shadow-sm border-0" style={{ borderRadius: 18 }}>
                  <h5>Family Information</h5>
                  <Row>
                    <Col md={4}>
                      <Form.Group>
                        <Form.Label>Family Income</Form.Label>
                        <Form.Control
                          type="number"
                          value={form.family_income}
                          onChange={(e) => update("family_income", +e.target.value)}
                        />
                      </Form.Group>
                    </Col>

                    <Col md={4}>
                      <Form.Group>
                        <Form.Label>Family Members</Form.Label>
                        <Form.Control
                          type="number"
                          value={form.family_members}
                          onChange={(e) => update("family_members", +e.target.value)}
                        />
                      </Form.Group>
                    </Col>

                    <Col md={4}>
                      <Form.Group>
                        <Form.Label>Girl Child</Form.Label>
                        <Form.Select
                          value={form.girlchild}
                          onChange={(e) => update("girlchild", e.target.value)}
                        >
                          <option>No</option>
                          <option>Yes</option>
                        </Form.Select>
                      </Form.Group>
                    </Col>
                  </Row>
                </Card>

                {/* Support */}
                <Card className="p-3 mb-3 shadow-sm border-0" style={{ borderRadius: 18 }}>
                  <h5>Support & Behaviour</h5>
                  <Row>
                    <Col md={4}>
                      <Form.Group>
                        <Form.Label>Communication Freq</Form.Label>
                        <Form.Control
                          type="number"
                          value={form.communication_freq}
                          onChange={(e) => update("communication_freq", +e.target.value)}
                        />
                      </Form.Group>
                    </Col>

                    <Col md={4}>
                      <Form.Group>
                        <Form.Label>Interest Level</Form.Label>
                        <Form.Control
                          type="number"
                          value={form.interest_lvl}
                          onChange={(e) => update("interest_lvl", +e.target.value)}
                        />
                      </Form.Group>
                    </Col>

                    <Col md={4}>
                      <Form.Group>
                        <Form.Label>Family Support</Form.Label>
                        <Form.Control
                          type="number"
                          value={form.family_support}
                          onChange={(e) => update("family_support", +e.target.value)}
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                </Card>

                <Button type="submit" disabled={submitting}>
                  {submitting ? "Predicting..." : "Predict"}
                </Button>
              </Col>

              {/* Right Panel */}
              <Col lg={4}>
                <Card className="p-3 shadow-sm border-0" style={{ borderRadius: 18 }}>
                  <h5>Prediction Result</h5>

                  {result ? (
                    <div className="mt-3">
                      <p>
                        <strong>Dropout Probability:</strong> {dropoutPct}%
                      </p>
                      <p>
                        <strong>Deservingness Score:</strong> {deservingScore}
                      </p>
                      <p>
                        <strong>Risk Tier:</strong>{" "}
                        <span
                          className={`badge ${
                            result.risk_tier === "HIGH"
                              ? "bg-danger"
                              : result.risk_tier === "MEDIUM"
                              ? "bg-warning text-dark"
                              : "bg-success"
                          }`}
                        >
                          {result.risk_tier}
                        </span>
                      </p>

                      {result.explanation && (
                        <div className="small mt-3">
                          <p><strong>Model Probability:</strong> {result.explanation.model_probability}</p>
                          <p><strong>Rule Probability:</strong> {result.explanation.rule_probability}</p>
                          <p>
                            <strong>Reasons:</strong>{" "}
                            {(result.explanation.human_readable_reasons || []).join(", ")}
                          </p>
                        </div>
                      )}
                    </div>
                  ) : (
                    <p className="text-muted mt-3">No result yet.</p>
                  )}
                </Card>
              </Col>
            </Row>
          </Form>
        </Container>
      </div>
    </>
  );
}
