// src/pages/PredictSingle.jsx
import React, { useState } from "react";
import TopNav from "../components/TopNav";
import {
  Container,
  Row,
  Col,
  Card,
  Form,
  Button,
  Badge,
  OverlayTrigger,
  Tooltip,
  ProgressBar,
} from "react-bootstrap";
import { predictSingle } from "../services/api";
import { toast } from "react-toastify";

/**
 * PredictSingle.jsx
 * - Keeps the UI you provided.
 * - Fixes runtime crash caused by explanation.human_readable_reasons not being an array.
 * - Ensures we send the payload keys the ML backend expects (raw-like payload).
 * - Parses various explanation formats safely and renders them.
 */

/* ------------------ UI HELPERS ------------------ */
const InfoPill = ({ label, value, variant = "secondary" }) => {
  if (!value && value !== 0) return null;
  return (
    <Badge
      bg={variant}
      pill
      className="me-2 mb-2"
      style={{ fontSize: "0.7rem", fontWeight: 600 }}
    >
      {label}: {value}
    </Badge>
  );
};

const Gauge = ({ label, value, suffix = "", variant = "info" }) => {
  const numeric = Number(value) || 0;
  const clamped = Math.max(0, Math.min(100, numeric));

  return (
    <div className="mb-3">
      <div className="d-flex justify-content-between align-items-center">
        <span className="small text-muted">{label}</span>
        <span className="small">
          <strong>{clamped.toFixed(1)}</strong>
          {suffix}
        </span>
      </div>
      <ProgressBar
        now={clamped}
        variant={variant}
        style={{ height: 8 }}
        className="rounded-pill mt-1"
      />
    </div>
  );
};

/* ------------------ Helpers: build payload & parse explanation ------------------ */

/**
 * Build payload matching the ML API expected keys.
 * We keep values taken from form, but map some alternate keys to the exact names
 * in the sample payload (motivational_score, communication_freq, interest_lvl, family_support).
 */
const buildPayloadForModel = (form) => {
  return {
    name: form.name || "",
    district: form.district || "",

    cutoff: Number(form.cutoff || 0),
    preferred_location: form.preferred_location || "any",
    preferred_course: form.preferred_course || "any",
    family_income_tier: form.family_income_tier || "any",

    family_income: Number(form.family_income || 0),
    family_members: Number(form.family_members || 0),

    marks_10: Number(form.marks_10 || 0),
    marks_11: Number(form.marks_11 || 0),
    marks_12: Number(form.marks_12 || 0),

    motivational_score:
      Number(form.motivational_score ?? form.motivation_level ?? 0),

    attendance_rate: Number(form.attendance_rate || 0),

    // backend expects communication_freq, interest_lvl, family_support names
    communication_freq:
      Number(form.communication_freq ?? form.communication_frequency ?? 0),
    interest_lvl: Number(form.interest_lvl ?? form.interest_level ?? 0),
    family_support:
      Number(form.family_support ?? form.family_support_level ?? 0),

    academic_score: Number(form.academic_score || 0),

    orphan: (form.orphan || "no").toString(),
    single_parent: (form.single_parent || "no").toString(),
    first_graduate: (form.first_graduate || "no").toString(),
    girlchild: (form.girlchild || form.girl_child || "no").toString(),

    attitude: form.attitude || "",

    // keep legacy / optional fields if present
    passed_out_10: Number(form.passed_out_10 || 0),
    passed_out_11: Number(form.passed_out_11 || 0),
    passed_out_12: Number(form.passed_out_12 || 0),
  };
};

/**
 * Parse explanation returned by ML.
 * Handles:
 *  - already an object
 *  - JSON string
 *  - stringified map like "{a=1, b=2}"
 *  - nested string fields that contain arrays encoded as strings
 *
 * Returns a normalized object where:
 *  - human_readable_reasons is guaranteed to be an array (or empty array)
 *  - numeric probabilities are numbers when possible
 */
const normalizeExplanation = (expl) => {
  const out = {
    raw: null,
    human_readable_reasons: [],
    model_probability: undefined,
    rule_probability: undefined,
    global_top_model_features: [],
    final_probability_used: undefined,
  };

  if (!expl) return out;

  // if it's already an object, shallow copy it
  let parsed = null;
  if (typeof expl === "object") {
    parsed = expl;
  } else if (typeof expl === "string") {
    // Try to parse JSON first
    try {
      parsed = JSON.parse(expl);
    } catch {
      // Try to convert Python/dump style {a=1, b=2} or {a:1, b:2} into JSON-ish
      try {
        const cleaned = expl
          .trim()
          .replace(/([a-zA-Z0-9_]+)\s*=/g, '"$1":')
          .replace(/([\{,])\s*'([^']+)'/g, '$1"$2"')
          .replace(/([\{,])\s*([^"'\s\{\}\[\],:]+)\s*:/g, '$1"$2":')
          .replace(/'/g, '"');
        parsed = JSON.parse(cleaned);
      } catch {
        // give up â€” put raw string
        parsed = { raw: expl };
      }
    }
  } else {
    // unknown type
    parsed = { raw: String(expl) };
  }

  out.raw = parsed;

  // helper to coerce maybe-array values to actual arrays
  const ensureArray = (v) => {
    if (!v && v !== 0) return [];
    if (Array.isArray(v)) return v;
    if (typeof v === "string") {
      // try parse JSON array
      try {
        const p = JSON.parse(v);
        if (Array.isArray(p)) return p;
      } catch {}
      // split on common separators
      // first try "],[" style inside quotes (already handled), else split by `,` but keep sentences
      // try splitting on `., ` or `.` followed by space where text seems sentence-like
      if (v.includes(".,") || v.match(/\.\s*[A-Z]/)) {
        // split on `. ` and re-add the dot for readability
        return v
          .split(/\.\s+/)
          .map((s) => s.trim())
          .filter(Boolean)
          .map((s, i, arr) => (s.endsWith(".") ? s : arr.length > 1 ? s + "." : s));
      }
      // fallback: split on commas
      return v
        .split(",")
        .map((s) => s.trim())
        .filter(Boolean);
    }
    // fallback single item
    return [v];
  };

  // pull common keys safely
  if (parsed) {
    // human_readable_reasons (many formats)
    if (parsed.human_readable_reasons !== undefined) {
      out.human_readable_reasons = ensureArray(parsed.human_readable_reasons);
    } else if (parsed.reasons !== undefined) {
      out.human_readable_reasons = ensureArray(parsed.reasons);
    }

    // model_probability and rule_probability may be string or number
    const toNum = (v) => {
      if (v === undefined || v === null) return undefined;
      if (typeof v === "number") return v;
      if (typeof v === "string") {
        const n = parseFloat(v.replace(/[^\d.-]/g, ""));
        return Number.isFinite(n) ? n : undefined;
      }
      return undefined;
    };

    out.model_probability = toNum(parsed.model_probability ?? parsed.model_prob ?? parsed.model_probablity);
    out.rule_probability = toNum(parsed.rule_probability ?? parsed.rule_prob);
    out.final_probability_used = toNum(parsed.final_probability_used ?? parsed.final_probability);

    // top features may come as stringified list or real array
    if (parsed.global_top_model_features) {
      out.global_top_model_features = ensureArray(parsed.global_top_model_features).map(String);
    } else if (parsed.top_features) {
      out.global_top_model_features = ensureArray(parsed.top_features).map(String);
    }
  }

  return out;
};

/* ------------------ Component ------------------ */
export default function PredictSingle() {
  const [form, setForm] = useState({
    // fields that user provided in the working version (defaults)
    name: "Test Student",
    district: "Chennai",
    cutoff: 2000,
    preferred_location: "chennai",
    preferred_course: "any",
    family_income_tier: "any",

    family_income: 5000,
    family_members: 4,

    marks_10: 450,
    marks_11: 430,
    marks_12: 440,

    motivational_score: 78,
    attendance_rate: 85,
    communication_freq: 6,
    interest_lvl: 7,
    family_support: 4,

    academic_score: 90,

    orphan: "no",
    single_parent: "no",
    first_graduate: "no",
    girlchild: "no",
    attitude: "positive",

    // extra legacy fields used by UI
    passed_out_10: 2022,
    passed_out_11: 2023,
    passed_out_12: 2024,
    private_or_govt_school: "Government",
    siblings: "No",
    siblings_details: "",
    siblings_work_or_college: "",
    rent_or_own: "Rent",
    property_owned: "No",
    parents_occupation: "Labour",
    willing_hostel: "Yes",
    any_scholarship: "No",
    first_graduate: "Yes",
    scholarship_eligibility: "Yes",
    communication_frequency: "Medium",
    family_support_level: "Medium",
    extra_curricular: "No",
    interest_level: "Medium",
    attitude: "Respectful",
    home_address: "",
    school_fee_6_to_12: "10k-20k",
  });

  const [result, setResult] = useState(null);
  const [normalizedExplanation, setNormalizedExplanation] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  const update = (key, value) => {
    setForm((prev) => ({ ...prev, [key]: value }));
  };

  const submit = async (e) => {
    if (e && e.preventDefault) e.preventDefault();
    try {
      setSubmitting(true);
      const payload = buildPayloadForModel(form);
      const res = await predictSingle(payload);

      // store raw result
      setResult(res || null);

      // normalize explanation
      const explRaw = res?.explanation ?? res?.explain ?? res?.explanations ?? null;
      const normalized = normalizeExplanation(explRaw);
      setNormalizedExplanation(normalized);

      toast.success("Prediction success");
    } catch (err) {
      console.error("Prediction error:", err);
      toast.error("Prediction failed");
      setResult({ error: String(err?.message ?? err) });
      setNormalizedExplanation(null);
    } finally {
      setSubmitting(false);
    }
  };

  const pageBackground =
    "linear-gradient(180deg, #eff6ff 0%, #ffffff 40%, #f9fafb 100%)";
  const headerGradient =
    "linear-gradient(135deg, rgba(248,250,252,1) 0%, rgba(239,246,255,1) 40%, rgba(219,234,254,1) 100%)";

  const dropoutPct = result
    ? (Number(result.dropout_probability || result.dropout_prob || 0) * 100).toFixed(1)
    : "0.0";
  const deservingScore = result
    ? Number(result.deservingness_score || result.deserving_score || 0).toFixed(2)
    : "0.00";

  return (
    <>
      <TopNav />
      <div
        style={{
          minHeight: "100vh",
          background: pageBackground,
          fontFamily: '"Inter", system-ui, -apple-system, BlinkMacSystemFont',
        }}
      >
        <Container className="py-4">
          {/* HEADER STRIP */}
          <Row className="mb-3">
            <Col>
              <Card
                className="border-0 shadow-sm"
                style={{ borderRadius: 18, background: headerGradient }}
              >
                <Card.Body className="d-flex justify-content-between flex-wrap align-items-center">
                  <div className="mb-2 mb-md-0">
                    <h3
                      className="mb-1"
                      style={{ color: "#111827", fontWeight: 700 }}
                    >
                      Single Student Prediction
                    </h3>
                    <small className="text-muted">
                      Enter a studentâ€™s academic, family and support details,
                      then let the model estimate dropout risk and
                      deservingness.
                    </small>
                  </div>
                  <div className="text-end">
                    <InfoPill label="Default district" value={form.district} variant="primary" />
                    <InfoPill label="School type" value={form.private_or_govt_school} variant="info" />
                    <div className="small text-muted mt-1">Uses the same schema as your batch CSV.</div>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          </Row>

          <Form onSubmit={submit}>
            <Row className="g-4">
              {/* LEFT big form */}
              <Col lg={8}>
                {/* ACADEMIC CARD */}
                <Card className="p-3 mb-3 shadow-sm border-0" style={{ borderRadius: 18 }}>
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <div>
                      <h5 className="mb-0">Academic Information</h5>
                      <small className="text-muted">Scores and marks directly influence dropout probability.</small>
                    </div>
                    <OverlayTrigger
                      placement="left"
                      overlay={<Tooltip id="ac-tip">Higher academic and attendance values usually reduce dropout risk and increase deservingness score.</Tooltip>}
                    >
                      <span className="badge bg-light text-secondary" style={{ cursor: "help" }}>?</span>
                    </OverlayTrigger>
                  </div>

                  <Row>
                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Academic Score</Form.Label>
                        <Form.Control type="number" value={form.academic_score} onChange={(e) => update("academic_score", +e.target.value)} />
                      </Form.Group>
                    </Col>

                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Motivation Level (1â€“5)</Form.Label>
                        <Form.Control type="number" value={form.motivation_level} onChange={(e) => update("motivation_level", +e.target.value)} />
                      </Form.Group>
                    </Col>

                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Attendance Rate (%)</Form.Label>
                        <Form.Control type="number" value={form.attendance_rate} onChange={(e) => update("attendance_rate", +e.target.value)} />
                      </Form.Group>
                    </Col>
                  </Row>

                  <Row>
                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Marks 10</Form.Label>
                        <Form.Control type="number" value={form.marks_10} onChange={(e) => update("marks_10", +e.target.value)} />
                      </Form.Group>
                    </Col>
                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Marks 11</Form.Label>
                        <Form.Control type="number" value={form.marks_11} onChange={(e) => update("marks_11", +e.target.value)} />
                      </Form.Group>
                    </Col>
                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Marks 12</Form.Label>
                        <Form.Control type="number" value={form.marks_12} onChange={(e) => update("marks_12", +e.target.value)} />
                      </Form.Group>
                    </Col>
                  </Row>

                  <Row>
                    <Col md={4}>
                      <Form.Group className="mb-2">
                        <Form.Label>10th Passed Out Year</Form.Label>
                        <Form.Control type="number" value={form.passed_out_10} onChange={(e) => update("passed_out_10", +e.target.value)} />
                      </Form.Group>
                    </Col>
                    <Col md={4}>
                      <Form.Group className="mb-2">
                        <Form.Label>11th Passed Out Year</Form.Label>
                        <Form.Control type="number" value={form.passed_out_11} onChange={(e) => update("passed_out_11", +e.target.value)} />
                      </Form.Group>
                    </Col>
                    <Col md={4}>
                      <Form.Group className="mb-2">
                        <Form.Label>12th Passed Out Year</Form.Label>
                        <Form.Control type="number" value={form.passed_out_12} onChange={(e) => update("passed_out_12", +e.target.value)} />
                      </Form.Group>
                    </Col>
                  </Row>

                  <Row className="mt-2">
                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Cutoff (for 200)</Form.Label>
                        <Form.Control type="number" value={form.cutoff} onChange={(e) => update("cutoff", +e.target.value)} placeholder="e.g. 180" />
                        <Form.Text className="small text-muted">Cutoff is used by rules: (&gt;193 â†’ VERY HIGH; 150&lt;cutoff&lt;193 â†’ no risk; â‰¤150 â†’ risk).</Form.Text>
                      </Form.Group>
                    </Col>

                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Preferred Location</Form.Label>
                        <Form.Control value={form.preferred_location} onChange={(e) => update("preferred_location", e.target.value)} placeholder='e.g. "any" or "Chennai"' />
                      </Form.Group>
                    </Col>

                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Preferred Course</Form.Label>
                        <Form.Control value={form.preferred_course} onChange={(e) => update("preferred_course", e.target.value)} placeholder='e.g. "cse", "aiml", "any"' />
                      </Form.Group>
                    </Col>
                  </Row>
                </Card>

                {/* FAMILY CARD */}
                <Card className="p-3 mb-3 shadow-sm border-0" style={{ borderRadius: 18 }}>
                  <h5>Family Information</h5>
                  <small className="text-muted">Income, support and household structure are strong indicators in the model.</small>

                  <Row className="mt-2">
                    <Col md={6}>
                      <Form.Group className="mb-3">
                        <Form.Label>Family Income</Form.Label>
                        <Form.Control type="number" value={form.family_income} onChange={(e) => update("family_income", +e.target.value)} />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group className="mb-3">
                        <Form.Label>Family Members</Form.Label>
                        <Form.Control type="number" value={form.family_members} onChange={(e) => update("family_members", +e.target.value)} />
                      </Form.Group>
                    </Col>
                  </Row>

                  <Row>
                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Orphan</Form.Label>
                        <Form.Select value={form.orphan} onChange={(e) => update("orphan", e.target.value)}>
                          <option>no</option>
                          <option>yes</option>
                        </Form.Select>
                      </Form.Group>
                    </Col>
                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Single Parent</Form.Label>
                        <Form.Select value={form.single_parent} onChange={(e) => update("single_parent", e.target.value)}>
                          <option>no</option>
                          <option>yes</option>
                        </Form.Select>
                      </Form.Group>
                    </Col>
                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Girl Child</Form.Label>
                        <Form.Select value={form.girlchild} onChange={(e) => update("girlchild", e.target.value)}>
                          <option>no</option>
                          <option>yes</option>
                        </Form.Select>
                      </Form.Group>
                    </Col>
                  </Row>

                  <Row>
                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Siblings</Form.Label>
                        <Form.Select value={form.siblings} onChange={(e) => update("siblings", e.target.value)}>
                          <option>No</option>
                          <option>Yes</option>
                        </Form.Select>
                      </Form.Group>
                    </Col>
                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Siblings Details</Form.Label>
                        <Form.Control value={form.siblings_details} onChange={(e) => update("siblings_details", e.target.value)} />
                      </Form.Group>
                    </Col>
                    <Col md={4}>
                      <Form.Group className="mb-3">
                        <Form.Label>Siblings Work / College</Form.Label>
                        <Form.Control value={form.siblings_work_or_college} onChange={(e) => update("siblings_work_or_college", e.target.value)} />
                      </Form.Group>
                    </Col>
                  </Row>
                </Card>

                {/* SCHOOL & SUPPORT */}
                <Row>
                  <Col md={6}>
                    <Card className="p-3 mb-3 shadow-sm border-0" style={{ borderRadius: 18 }}>
                      <h5>School Information</h5>
                      <small className="text-muted">Government / Private and district help the model segment context.</small>

                      <Form.Group className="mb-3 mt-2">
                        <Form.Label>District</Form.Label>
                        <Form.Control value={form.district} onChange={(e) => update("district", e.target.value)} />
                      </Form.Group>

                      {["school_type_10", "school_type_11", "school_type_12"].map((k) => (
                        <Form.Group className="mb-3" key={k}>
                          <Form.Label>{k.replace(/_/g, " ").toUpperCase()}</Form.Label>
                          <Form.Select value={form[k]} onChange={(e) => update(k, e.target.value)}>
                            <option>Government</option>
                            <option>Private</option>
                          </Form.Select>
                        </Form.Group>
                      ))}

                      <Form.Group className="mb-3">
                        <Form.Label>Overall School Type (6â€“12)</Form.Label>
                        <Form.Select value={form.private_or_govt_school} onChange={(e) => update("private_or_govt_school", e.target.value)}>
                          <option>Government</option>
                          <option>Private</option>
                        </Form.Select>
                      </Form.Group>
                    </Card>
                  </Col>

                  <Col md={6}>
                    <Card className="p-3 mb-3 shadow-sm border-0" style={{ borderRadius: 18 }}>
                      <h5>Behaviour & Support</h5>
                      <small className="text-muted">These qualitative fields strongly impact deservingness.</small>

                      <Form.Group className="mb-3 mt-2">
                        <Form.Label>Family Support</Form.Label>
                        <Form.Select value={form.family_support} onChange={(e) => update("family_support", e.target.value)}>
                          <option>Low</option>
                          <option>Medium</option>
                          <option>High</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group className="mb-3">
                        <Form.Label>Communication Frequency</Form.Label>
                        <Form.Select value={form.communication_frequency} onChange={(e) => update("communication_frequency", e.target.value)}>
                          <option>Low</option>
                          <option>Medium</option>
                          <option>High</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group className="mb-3">
                        <Form.Label>Interest Level</Form.Label>
                        <Form.Select value={form.interest_level} onChange={(e) => update("interest_level", e.target.value)}>
                          <option>Low</option>
                          <option>Medium</option>
                          <option>High</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group className="mb-3">
                        <Form.Label>Extra-curricular</Form.Label>
                        <Form.Select value={form.extra_curricular} onChange={(e) => update("extra_curricular", e.target.value)}>
                          <option>No</option>
                          <option>Yes</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group className="mb-3">
                        <Form.Label>Attitude</Form.Label>
                        <Form.Control value={form.attitude} onChange={(e) => update("attitude", e.target.value)} />
                      </Form.Group>
                    </Card>
                  </Col>
                </Row>

                {/* HOUSING / SCHOLARSHIP */}
                <Row>
                  <Col md={6}>
                    <Card className="p-3 mb-3 shadow-sm border-0" style={{ borderRadius: 18 }}>
                      <h5>Housing & Assets</h5>
                      <Form.Group className="mb-3 mt-2">
                        <Form.Label>Rent or Own</Form.Label>
                        <Form.Select value={form.rent_or_own} onChange={(e) => update("rent_or_own", e.target.value)}>
                          <option>Rent</option>
                          <option>Own</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group className="mb-3">
                        <Form.Label>Property Owned</Form.Label>
                        <Form.Select value={form.property_owned} onChange={(e) => update("property_owned", e.target.value)}>
                          <option>No</option>
                          <option>Yes</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group className="mb-3">
                        <Form.Label>Parents Occupation</Form.Label>
                        <Form.Control value={form.parents_occupation} onChange={(e) => update("parents_occupation", e.target.value)} />
                      </Form.Group>

                      <Form.Group className="mb-3">
                        <Form.Label>Home Address (optional)</Form.Label>
                        <Form.Control as="textarea" rows={2} value={form.home_address} onChange={(e) => update("home_address", e.target.value)} />
                      </Form.Group>
                    </Card>
                  </Col>

                  <Col md={6}>
                    <Card className="p-3 mb-3 shadow-sm border-0" style={{ borderRadius: 18 }}>
                      <h5>Scholarship & Eligibility</h5>

                      <Form.Group className="mb-3 mt-2">
                        <Form.Label>Any Scholarship</Form.Label>
                        <Form.Select value={form.any_scholarship} onChange={(e) => update("any_scholarship", e.target.value)}>
                          <option>No</option>
                          <option>Yes</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group className="mb-3">
                        <Form.Label>First Graduate</Form.Label>
                        <Form.Select value={form.first_graduate} onChange={(e) => update("first_graduate", e.target.value)}>
                          <option>Yes</option>
                          <option>No</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group className="mb-3">
                        <Form.Label>Scholarship Eligibility</Form.Label>
                        <Form.Select value={form.scholarship_eligibility} onChange={(e) => update("scholarship_eligibility", e.target.value)}>
                          <option>Yes</option>
                          <option>No</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group className="mb-3">
                        <Form.Label>Willing for Hostel</Form.Label>
                        <Form.Select value={form.willing_hostel} onChange={(e) => update("willing_hostel", e.target.value)}>
                          <option>Yes</option>
                          <option>No</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group className="mb-3">
                        <Form.Label>School Fee (6â€“12)</Form.Label>
                        <Form.Select value={form.school_fee_6_to_12} onChange={(e) => update("school_fee_6_to_12", e.target.value)}>
                          <option>0-10k</option>
                          <option>10k-20k</option>
                          <option>20k-40k</option>
                          <option>40k+</option>
                        </Form.Select>
                      </Form.Group>
                    </Card>
                  </Col>
                </Row>

                <Button type="submit" className="mt-2 mb-4" disabled={submitting}>
                  {submitting ? "Predicting..." : "Predict"}
                </Button>
              </Col>

              {/* RIGHT panel: summary + result */}
              <Col lg={4}>
                <Card className="p-3 mb-3 shadow-sm border-0" style={{ borderRadius: 18 }}>
                  <h5 className="mb-1">Input Summary</h5>
                  <small className="text-muted">Quick glance at what you are about to send to the model.</small>

                  <div className="mt-3">
                    <InfoPill label="Academic" value={form.academic_score} variant="primary" />
                    <InfoPill label="Motivation" value={form.motivation_level} variant="info" />
                    <InfoPill label="Attendance" value={`${form.attendance_rate}%`} variant="success" />
                    <InfoPill label="Family income" value={form.family_income} variant="warning" />
                    <InfoPill label="Cutoff" value={form.cutoff} variant="dark" />
                    <InfoPill label="Preferred course" value={form.preferred_course} variant="secondary" />
                  </div>

                  <div className="mt-3">
                    <Gauge label="Academic score (approx)" value={form.academic_score} suffix="/100" variant="primary" />
                    <Gauge label="Motivation (scaled)" value={((form.motivation_level || 0) / 5) * 100} suffix="/100" variant="info" />
                    <Gauge label="Attendance rate" value={form.attendance_rate} suffix="%" variant="success" />
                  </div>
                </Card>

                <Card className="p-3 shadow-sm border-0" style={{ borderRadius: 18 }}>
                  <h5 className="mb-1">Prediction Result</h5>
                  <small className="text-muted">Results are returned directly from the ML API.</small>

                  {result ? (
                    <div className="mt-3">
                      <p className="mb-1">
                        <strong>Dropout Probability:</strong>{" "}
                        <span style={{ color: "#b91c1c" }}>{dropoutPct}%</span>
                      </p>
                      <p className="mb-1">
                        <strong>Deservingness Score:</strong>{" "}
                        <span style={{ color: "#16a34a" }}>{deservingScore}</span>
                      </p>
                      <p className="mb-2">
                        <strong>Risk Tier:</strong>{" "}
                        <span className={`badge ${result.risk_tier === "HIGH" ? "bg-danger" : result.risk_tier === "MEDIUM" ? "bg-warning text-dark" : "bg-success"}`}>
                          {result.risk_tier}
                        </span>
                      </p>

                      {normalizedExplanation ? (
                        <div className="mt-2 small">
                          {normalizedExplanation.model_probability !== undefined && (
                            <p className="mb-1">
                              <strong>Model Probability:</strong>{" "}
                              {(Number(normalizedExplanation.model_probability) * 100).toFixed(1)}%
                            </p>
                          )}

                          {normalizedExplanation.rule_probability !== undefined && (
                            <p className="mb-1">
                              <strong>Rule Probability:</strong>{" "}
                              {(Number(normalizedExplanation.rule_probability) * 100).toFixed(1)}%
                            </p>
                          )}

                          {Array.isArray(normalizedExplanation.human_readable_reasons) &&
                          normalizedExplanation.human_readable_reasons.length > 0 ? (
                            <div className="mt-2">
                              <strong>Reasons:</strong>
                              <ul className="mt-1">
                                {normalizedExplanation.human_readable_reasons.map((r, i) => (
                                  <li key={i}>{r}</li>
                                ))}
                              </ul>
                            </div>
                          ) : (
                            normalizedExplanation.raw && (
                              <div className="mt-2">
                                <strong>Explanation:</strong>
                                <div className="small">{String(normalizedExplanation.raw)}</div>
                              </div>
                            )
                          )}

                          {Array.isArray(normalizedExplanation.global_top_model_features) &&
                            normalizedExplanation.global_top_model_features.length > 0 && (
                              <div className="mt-2">
                                <strong>Model Top Features:</strong>{" "}
                                {normalizedExplanation.global_top_model_features.join(", ")}
                              </div>
                            )}
                        </div>
                      ) : (
                        <div className="small text-muted mt-2">No explanation returned.</div>
                      )}

                      <p className="small text-muted mb-0 mt-3">
                        Use this prediction as a decision-support tool to identify students needing additional financial and emotional support.
                      </p>
                    </div>
                  ) : (
                    <p className="text-muted mt-3">No result yet. Fill in the form and click <strong>Predict</strong> to get dropout probability and deservingness score.</p>
                  )}
                </Card>
              </Col>
            </Row>
          </Form>
        </Container>
      </div>
    </>
  );
}
